# Generate samples for each category
for category in categories:
    if category == 'spam':
        text = np.random.choice(spam_text, num_samples)
    elif category == 'phishing':
        text = np.random.choice(phishing_text, num_samples)
    elif category == 'malware distribution':
        text = np.random.choice(malware_text, num_samples)
    elif category == 'bot attacks':
        text = np.random.choice(bot_text, num_samples)
    else:
        text = []
    category_data = pd.DataFrame({'text': text, 'category': category})
    data = pd.concat([data, category_data])

# Shuffle the dataset
data = data.sample(frac=1).reset_index(drop=True)

# Save the dataset to a CSV file
data.to_csv('cyberattacks_dataset.csv', index=False)

